@concat('{
    "apiVersion": "batch/v1",
    "kind": "Job",
    "metadata": {
      "name": "aoi-cv-task",
      "namespace": "vision",
      "labels": {
        "run_id": "',pipeline().RunId, '",
      }
    },
    "spec": {
      "ttlSecondsAfterFinished": 5,
      "template": {
        "spec": {
          "containers": [
            {
              "name": "aoi-cv-task-xyz",
              "image": "', activity('Read Spec Document').output['runStatus'].output.sink.value[0]['algImageName'] ,'",
              "env": [
                {
                  "name": "APP_INPUT_DIR",
                  "value": "', activity('Read Spec Document').output['runStatus'].output.sink.value[0]['mountedDirectory'] , '/',pipeline().parameters.Prefix,'/' , activity('Read Spec Document').output['runStatus'].output.sink.value[0]['submissionDirectory'],'"
                },
                {
                  "name": "APP_OUTPUT_DIR",
                  "value": "', activity('Read Spec Document').output['runStatus'].output.sink.value[0]['mountedDirectory'] , '/',pipeline().parameters.Prefix,'/' , activity('Read Spec Document').output['runStatus'].output.sink.value[0]['resultsDirectory'],'"
                },
                {
                  "name": "APP_CONFIG_DIR",
                  "value": "', activity('Read Spec Document').output['runStatus'].output.sink.value[0]['mountedDirectory'] , '/',pipeline().parameters.Prefix,'/' , activity('Read Spec Document').output['runStatus'].output.sink.value[0]['contextFileName'],'"
                }
              ],
              "volumeMounts": [
                {
                  "name": "azure",
                  "mountPath": "', activity('Read Spec Document').output['runStatus'].output.sink.value[0]['mountedDirectory'], '"
                }
              ]
            }
          ],
          "volumes": [
            {
              "name": "azure",
              "persistentVolumeClaim": {
                "claimName": "', pipeline().parameters.PersistentVolumeClaim, '"
              }
            }
          ],
          "automountServiceAccountToken": false,
          "restartPolicy": "Never"
        }
      }
    }
}')
